import React, { useEffect } from 'react';
import './Contact.css';
import Kaleidoscope from 'kaleidoscope-effect-library';
import Navbar from './Navbar';
const Contact = () => {
  useEffect(() => {
    const handleScroll = () => {
      const sections = document.querySelectorAll('.contact-section');
      sections.forEach(section => {
        const rect = section.getBoundingClientRect();
        if (rect.top >= 0 && rect.bottom <= window.innerHeight) {
          section.classList.add('in-view');
          // Apply kaleidoscope effect
          new Kaleidoscope(section, { effect: 'kaleidoscope' });
        } else {
          section.classList.remove('in-view');
        }
      });
    };

    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <div className="contact-container">
      <div className="contact-section" id="phone">
        <i className="fas fa-phone-alt icon"></i>
        <h2>Phone</h2>
        <p>+123 456 7890</p>
      </div>
      <div className="contact-section" id="email">
        <i className="fas fa-envelope icon"></i>
        <h2>Email</h2>
        <p>example@example.com</p>
      </div>
      <div className="contact-section" id="social-media">
        <i className="fas fa-share-alt icon"></i>
        <h2>Social Media</h2>
        <p>
          <a href="https://www.linkedin.com/in/yourprofile" target="_blank" rel="noopener noreferrer">LinkedIn</a><br />
          <a href="https://www.twitter.com/yourprofile" target="_blank" rel="noopener noreferrer">X (formerly Twitter)</a>
        </p>
      </div>
    </div>
  );
};

export default Contact;
