import React, { useState, useEffect } from 'react';
import './Contact.css';

const Contact = () => {
  const [currentSection, setCurrentSection] = useState(0);
  const sections = ["section1", "section2", "section3"]; // Assuming there are 3 sections

  const scrollToSection = (index) => {
    setCurrentSection(index);
    document.getElementById(sections[index]).scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    const handleScroll = (e) => {
      e.preventDefault();
    };
    window.addEventListener('wheel', handleScroll, { passive: false });
    return () => {
      window.removeEventListener('wheel', handleScroll, { passive: false });
    };
  }, []);

  return (
    <div className="contact-container">
      {sections.map((section, index) => (
        <div
          key={section}
          id={section}
          className={`contact-section ${currentSection === index ? 'in-view' : ''}`}
        >
          <i className="icon fas fa-address-card"></i>
          <h2>{`Section ${index + 1}`}</h2>
          <p>{`Content for ${section}`}</p>
          {index > 0 && (
            <button
              className="nav-button up"
              onClick={() => scrollToSection(index - 1)}
            >
              ↑
            </button>
          )}
          {index < sections.length - 1 && (
            <button
              className="nav-button down"
              onClick={() => scrollToSection(index + 1)}
            >
              ↓
            </button>
          )}
        </div>
      ))}
    </div>
  );
};

export default Contact;
