// Contact.js
import React, { useEffect } from 'react';
import './Contact.css';
import Kaleidoscope from './Kaleidoscope';

const Contact = () => {
  useEffect(() => {
    const sections = document.querySelectorAll('.contact-section');
    let isThrottled = false;

    const handleScroll = () => {
      if (isThrottled) return;
      isThrottled = true;

      setTimeout(() => {
        isThrottled = false;
        let scrollY = window.scrollY;
        let closestSection = null;
        let minDistance = Infinity;

        sections.forEach(section => {
          const rect = section.getBoundingClientRect();
          const distance = Math.abs(rect.top);
          if (distance < minDistance) {
            minDistance = distance;
            closestSection = section;
          }
        });

        if (closestSection) {
          closestSection.scrollIntoView({ behavior: 'smooth' });
        }
      }, 200);
    };

    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  useEffect(() => {
    const handleScroll = () => {
      const sections = document.querySelectorAll('.contact-section');
      sections.forEach(section => {
        const rect = section.getBoundingClientRect();
        if (rect.top >= 0 && rect.bottom <= window.innerHeight) {
          section.classList.add('in-view');
        } else {
          section.classList.remove('in-view');
        }
      });
    };

    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <div className="contact-container">
      <div className="contact-section" id="phone">
        <Kaleidoscope />
        <i className="fas fa-phone-alt icon"></i>
        <h2>Phone</h2>
        <p>+123 456 7890</p>
      </div>
      <div className="contact-section" id="email">
        <Kaleidoscope />
        <i className="fas fa-envelope icon"></i>
        <h2>Email</h2>
        <p>example@example.com</p>
      </div>
      <div className="contact-section" id="social-media">
        <Kaleidoscope />
        <i className="fas fa-share-alt icon"></i>
        <h2>Social Media</h2>
        <p>
          <a href="https://www.linkedin.com/in/yourprofile" target="_blank" rel="noopener noreferrer">LinkedIn</a><br />
          <a href="https://www.twitter.com/yourprofile" target="_blank" rel="noopener noreferrer">X (formerly Twitter)</a>
        </p>
      </div>
    </div>
  );
};

export default Contact;
