import React, { useEffect, useState, useCallback } from 'react';
import './Contact.css';
import Kaleidoscope from './Kaleidoscope';

const Contact = () => {
  const [currentSection, setCurrentSection] = useState(0);

  const sections = [
    { id: 'phone', icon: 'fas fa-phone-alt', title: 'Phone', content: '+123 456 7890' },
    { id: 'email', icon: 'fas fa-envelope', title: 'Email', content: 'example@example.com' },
    { id: 'social-media', icon: 'fas fa-share-alt', title: 'Social Media', content: 
      <>
        <a href="https://www.linkedin.com/in/yourprofile" target="_blank" rel="noopener noreferrer">LinkedIn</a><br />
        <a href="https://www.twitter.com/yourprofile" target="_blank" rel="noopener noreferrer">X (formerly Twitter)</a>
      </>
    }
  ];

  const scrollToSection = useCallback((index) => {
    const section = document.getElementById(sections[index].id);
    if (section) {
      section.scrollIntoView({ behavior: 'smooth' });
      setCurrentSection(index);
    }
  }, [sections]);

  useEffect(() => {
    scrollToSection(0); // Scroll to the first section initially

    const handleScroll = () => {
      const sectionElements = document.querySelectorAll('.contact-section');
      sectionElements.forEach((section, index) => {
        const rect = section.getBoundingClientRect();
        if (rect.top >= 0 && rect.bottom <= window.innerHeight) {
          setCurrentSection(index);
        }
      });
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, [scrollToSection]);

  return (
    <div className="contact-container">
      {sections.map((section, index) => (
        <div className="contact-section" id={section.id} key={section.id}>
          <Kaleidoscope />
          <i className={`${section.icon} icon`}></i>
          <h2>{section.title}</h2>
          <p>{section.content}</p>
        </div>
      ))}
      {currentSection > 0 && (
        <button
          className="nav-button up-button"
          onClick={() => scrollToSection(currentSection - 1)}
        >
          <i className="fas fa-arrow-up"></i>
        </button>
      )}
      {currentSection < sections.length - 1 && (
        <button
          className="nav-button down-button"
          onClick={() => scrollToSection(currentSection + 1)}
        >
          <i className="fas fa-arrow-down"></i>
        </button>
      )}
    </div>
  );
};

export default Contact;
