import React, { useEffect } from 'react';
import './About.css';
import Navbar from './Navbar';

const sections = [
    {
      title: "About Me",
      content: "I am an Ethical hacker, Web Developer, and a Software Developer with a passion for creating secure and efficient applications. I'm also the CEO and sole founder of Data-Ject Solutions, a cybersecurity and software development company focused on AI, Software/Web Development, and Penetration Testing.",
      img: "/DilbertEsque"
    },
    {
      title: "Technical Skills",
      content: "I am proficient in various programming languages and frameworks such as Python, Java, C-Languages, Django, React, and Springboot. I also have a strong command over Unix and Unix-like systems including MacOS and Linux.",
      img: "/path/to/technical-skills.jpg"
    },
    {
      title: "Career Highlights",
      content: "As the Founder and CEO of Data-Ject Solutions, I've led numerous projects, including the development of 'anymodel', an AI chat model for software developers and cybersecurity enthusiasts.",
      img: "/path/to/career-highlights.jpg"
    },
    {
      title: "Notable Projects",
      content: "I have developed full-fledged applications for both iOS and Android using Flutter, with live backends written in Python. I also have extensive experience in web development, focusing on creating robust and scalable applications.",
      img: "/path/to/notable-projects.jpg"
    },
    {
      title: "Beyond Work",
      content: "Apart from my professional work, I have well-versed social skills and find it important to communicate effectively between management and team members. I am also passionate about continuous learning and staying updated with the latest in technology.",
      img: "/path/to/beyond-work.jpg"
    }
  ];

const About = () => {
  useEffect(() => {
    const handleScroll = () => {
      const elements = document.querySelectorAll('.section');
      elements.forEach((element) => {
        const rect = element.getBoundingClientRect();
        if (rect.top < window.innerHeight && rect.bottom > 0) {
          element.classList.add('visible');
        } else {
          element.classList.remove('visible');
        }
      });
    };

    window.addEventListener('scroll', handleScroll);
    handleScroll();

    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <div className="about-container">
      <Navbar />
      {sections.map((section, index) => (
        <div key={index} className="section">
          <img src={section.img} alt={`Section ${index + 1}`} className="section-image" />
          <div className="section-content">
            <h2>{section.title}</h2>
            <p>{section.content}</p>
          </div>
        </div>
      ))}
    </div>
  );
};

export default About;
