import React, { useEffect, useRef } from 'react';

const Kaleidoscope = () => {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const width = canvas.width = window.innerWidth;
    const height = canvas.height = window.innerHeight;

    const drawKaleidoscope = () => {
      ctx.clearRect(0, 0, width, height);
      const image = new Image();
      image.src = 'https://example.com/your-image.jpg'; // Replace with your image URL
      image.onload = () => {
        for (let i = 0; i < 360; i += 60) {
          ctx.save();
          ctx.translate(width / 2, height / 2);
          ctx.rotate((i * Math.PI) / 180);
          ctx.drawImage(image, -width / 2, -height / 2, width, height);
          ctx.restore();
        }
      };
    };

    drawKaleidoscope();
    const interval = setInterval(drawKaleidoscope, 1000 / 30); // Redraw every 30ms

    return () => clearInterval(interval);
  }, []);

  return <canvas ref={canvasRef} className="kaleidoscope-canvas"></canvas>;
};

export default Kaleidoscope;
